class WikipediaLinkHooker:
	"""Module for hooking wikipedia links for uncommon nouns. Can be generalized"""
	"""
	Involved attribute:text(as html)
	"""
	def __init__(self,text,window_size):
		self.text = text;
		self.pure_text = AnswerParser(self.text).getPureAnswerWOLinks()

		pos_model_file = "C://python34/ProjectDragonWolf/nlp_res/stanford_pos/models/english-bidirectional-distsim.tagger";
		pos_jar_file = "C://python34/ProjectDragonWolf/nlp_res/stanford_pos/stanford-postagger.jar";
		ner_model_file = "C://python34/ProjectDragonWolf/nlp_res/stanford_ner/classifiers/english.all.3class.distsim.crf.ser.gz"
		ner_jar_file = "C://python34/ProjectDragonWolf/nlp_res/stanford_ner/stanford-ner.jar"

		self.pos = StanfordPOSTagger(model_filename=pos_model_file,path_to_jar=pos_jar_file)
		self.ner = StanfordNERTagger(model_filename=ner_model_file,path_to_jar=ner_jar_file)
		self.window_size = window_size

	def dnd_tag(self,tokens):
		word_bag = json.loads(open("C://python34/ProjectDragonWolf/word_list.json","r").read())
		res = []
		for token in tokens:
			if token.lower().strip() in word_bag:
				res.append((token,"D"))
			else:
				res.append((token,"ND"))
		return res;

